import { Link } from 'react-router-dom';

const ProjectCard = ({project}) => {

  const tags = project.tags.map((tag, i) => {
    return <div key={i} className="badge  text-black bg-green-500">{tag}</div>;
  });

  return (
    <div className=" ">
    <figure><img src={project.images[0].path} alt={project.images[0].caption} /></figure>
    <div className="">
      <h2 className="text-4xl hover:underline">
        {project.title}
      </h2>
      <p>{project.description}</p>
      <div className=" ">
        {tags}
        <br/>
    <div className='w-full'>
        {(project.website) ? (<Link to={project.website} target="_blank" rel="noreferrer" className="btn mr-2 btn-primary">Website</Link>) : ""}

        {(project.github) ? (<Link to={project.github} target="_blank" rel="noreferrer" className="btn  btn-primary">GitHub</Link>) : ""}
        </div>
        <Link className='justify-end btn ' to={`/projects/${project.slug}`}>Take a look</Link>
      </div>
    </div>
    <div className="container-fluid bg-orange-600 py-4">
				<div className="grid grid-cols-3 gap-4 container">
					<div className="text-white col-span-1 font-medium text-2xl">
						<img
							className="w-full"
              src={require(`${project.images[0]?.path}`)}
							alt="github icon"
						/>
					</div>

					<div className="col-span-2 text-white text-4xl">
						My name is Jack I am a student and novice software developer. <br/>
						I have a wealth of experience in many different languages and Softwares, Python and JavaScript being my particular favorites.<br/>
						A veteran of the service industry, I have tried and tested interpersonal skills and a strong work ethic. <br/>
					</div>
				</div>
			</div>
  </div>

  );
};

export default ProjectCard;
