import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import projectsJSON from "../../assets/data/projects.json";
import { Link } from "react-router-dom";
import { Carousel } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const Show = () => {
  const [project, setProject] = useState(null);
  const { slug } = useParams();

  useEffect(() => {
    setProject(projectsJSON.find((proj) => proj.slug === slug));
  }, [slug]);

  if (!project) return <h1>Project doesn't exist</h1>;

  const tags = project.tags?.map((tag, i) => (
    <div key={i} className="badge badge-outline badge-primary">
      {tag}
    </div>
  ));

  return (


    <div className="container">
            <div className="w-64 carousel rounded-box">
  <div className="carousel-item w-full">
    <img src="https://daisyui.com/images/stock/photo-1559703248-dcaaec9fab78.jpg" className="w-full" alt="Tailwind CSS Carousel component" />
  </div> 
  <div className="carousel-item w-full">
    <img src="https://daisyui.com/images/stock/photo-1565098772267-60af42b81ef2.jpg" className="w-full" alt="Tailwind CSS Carousel component" />
  </div> 

</div>
      <div className="w-64 carousel rounded-box" >
        {project.images &&
          project.images.map((image, i) => (
            <div className="carousel-item w-full" key={i}>
              <img
                title={image.caption}
                src={image.path}
                className="d-block "
                alt={image.caption}
              />
            </div>
          ))}
      </div>




      <div className="mt-4">
        <p>
          <b>Title:</b> {project.title}
        </p>
        <p>
          <b>Description:</b> {project.description}
        </p>
        <p>
          <b>Date:</b> {project.date}
        </p>
        <p>
          <b>Tags:</b> {tags}
        </p>
        <p>
          <b>Website:</b> <a href={project.website}>website</a>
        </p>
        <p>
          <b>GitHub:</b> <a href={project.github}>github</a>
        </p>

        {project.demo ? (
          <Link to={`/projects/${project.slug}/demo`}>Demo</Link>
        ) : (
          <p>Demo goes here</p>
        )}
      </div>
    </div>
  );
};

export default Show;
