import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
// import projectsJSON from "../../assets/data/projects.json";
import { Link } from "react-router-dom";
import axios from "axios";
const Show = () => {
  const [project, setProject] = useState(null);
  const { slug } = useParams();

  const tags = project?.tags?.map((tag, i) => (
    <div key={i} className="badge badge-outline badge-primary">
      {tag}
    </div>
  ));


  // const [selectedSlug, setSelectedSlug] = useState("your-desired-slug");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://port-data-5e763-default-rtdb.europe-west1.firebasedatabase.app/projects.json', {
          params: {
            orderBy: '"slug"',
            equalTo: `"${selectedSlug}"`,
            limitToFirst: 1
          }
        });

        const fetchedProjects = response.data;

        // Assuming your response is an object with project IDs as keys
        const projectId = Object.keys(fetchedProjects)[0];
        const selectedProject = fetchedProjects[projectId];

        setProject(selectedProject);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [selectedSlug]);

  return (
    <div className="justify-items-center">
      <div className="container">
        {/* ... (Your existing code) */}
        <input
          type="text"
          placeholder="Enter slug"
          value={selectedSlug}
          onChange={(e) => setSelectedSlug(e.target.value)}
        />
        {/* ... (Your existing code) */}
      </div>
      {project && <ProjectCard project={project} />}
    </div>
  );
};
export default Show;
