import React, { useEffect, useState } from 'react';

const Contact = () => {
  const [isNavHidden, setNavHidden] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setNavHidden((prev) => window.scrollY > 0 && window.scrollY > prev);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>




        {/* Title w/ Full width background */}
        <div className="bg-off-white text-light pt-3 img width-auto">
          <div className="container-fluid">
            <div className="container">
              <div className="row pt-5">
                <div>
                  <p className="pt-5 mb-5 text-center text-dark display-1 display-title col-12 ">About Me</p>
                </div>
              </div>
            </div>
          </div>

          {/* Nested containers with full width backgrounds colours to give clear definition of different articles */}
          <div className="container-fluid bg-orange">
            <div className="container">
              <div className="row">
                <div className="my-3 my-md-5  col-12 col-md-6 ">
                  <img className="med-card cover" src={require('../assets/images/github-mark.png')} alt="Two men in a music studio" />
                </div>
                <h2 className="col-12 col-md-6  text-dark my-3 my-md-5">My name is Jack a </h2>
              </div>
            </div>
          </div>
          {/* Nested containers with full width backgrounds colours to give clear definition of different articles */}
<div className="container-fluid bg-dark">
  <div className="container">
    <div className="row">
      <h2 className="col-12 col-md-6 text-off-white my-3 my-md-5">
        Connecting with fans and industry professionals through the platform and promoting your music and shows through various channels such as social media and live performances will also be important. Finally, actively selling tickets to your shows through Jackmob is key to building a successful career as an artist.
      </h2>
      <div className="my-3 my-md-5 col-12 col-md-6">
      <img className="med-card cover" src={require('../assets/images/github-mark.png')} alt="Two men in a music studio" />
      </div>
    </div>
  </div>
</div>
{/* Nested containers with full width backgrounds colours to give clear definition of different articles */}
<div className="container-fluid bg-orange">
  <div className="container">
    <div className="row">
      <div className="my-3 my-md-5 col-12 col-md-6">
      <img className="med-card cover" src={require('../assets/images/github-mark.png')} alt="Two men in a music studio" />
      </div>
      <h2 className="col-12 col-md-6 text-dark my-3 my-md-5">
        All of these efforts require hard work and dedication, but with the right mindset and a willingness to put in the effort, it is possible to achieve your dreams of making it in the music industry.
      </h2>
    </div>
  </div>
</div>

          {/* ... (Repeat similar structure for other sections) */}

        </div>




    </>
  );
};

export default Contact;
