import React, { useState } from "react";
import { Link } from "react-router-dom";

const Sidebar = () => {
  const [open, setOpen] = useState(true);

  let Menus = [
    { title: "Dashboard", src: "Chart_fill" },
    { title: "Inbox", src: "Chat" },
    { title: "Accounts", src: "User", gap: true },
    { title: "Schedule", src: "Calendar" },
    { title: "Search", src: "Search" },
    { title: "Analytics", src: "Chart" },
    { title: "Files", src: "Folder", gap: true },
    { title: "Setting", src: "Setting" },
  ];

  Menus = Menus.map((menu, index) => (
    <li
      key={index}
      className={`flex rounded-md p-2 cursor-pointer hover:bg-light-white text-gray-300 text-sm items-center gap-x-4 ${
        menu.gap ? "mt-9" : "mt-2"
      } ${index === 0 ? "bg-light-white" : ""}`}
    >
      <img src={`./src/assets/${menu.src}.png`} alt={`${menu.title} Icon`} />
      <span className={`${!open && "hidden"} origin-left duration-200`}>
        {menu.title}
      </span>
    </li>
  ));

  return (
    <div className="flex bg-slate-500">
      <div
        className={`${open ? "w-72" : "w-20"} bg-dark-purple h-screen p-5 pt-8 relative duration-300`}
      >
        <div
          className={`absolute cursor-pointer -right-3 top-9 w-7 border-dark-purple border-2  ${!open && "rotate-180"}`}
          alt="Toggle Sidebar"
          onClick={() => setOpen(!open)}
        >
          <h1>&gt;</h1>
        </div>
        <div className="flex gap-x-4 items-center">
          <h1
            className={`text-white origin-left font-medium text-xl duration-200 ${!open && "scale-0"}`}
          >
            Jack
          </h1>
        </div>
        <ul className="pt-6">
          <li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4 ">
            <Link to="/">Home</Link>
          </li>
          <li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4 ">
            <Link to="/about">About</Link>
          </li>
          <li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4 ">
            <Link to="/projects">Projects</Link>
          </li>
          <li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4 ">
            <Link to="/contact">Contact</Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
