import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import projectsJSON from "../../assets/data/projects.json";
import { Link } from "react-router-dom";
import { Carousel } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const Show = () => {
  const [project, setProject] = useState(null);
  const { slug } = useParams();

  useEffect(() => {
    setProject(projectsJSON.find((proj) => proj.slug === slug));
  }, [slug]);

  if (!project) return <h1>Project doesn't exist</h1>;

  let imageCarousel = null;

  if (project.images) {
    const carouselItems = project.images.map((image, i) => (
      <Carousel.Item key={i}>
        <img
          title={image.caption}
          src={image.path}
          className="d-block w-100"
          alt={image.caption}
        />
      </Carousel.Item>
    ));

    imageCarousel = (
      <Carousel>
        {carouselItems}
      </Carousel>
    );
  }

  const tags = project.tags?.map((tag, i) => (
    <div key={i} className="badge badge-outline badge-primary">
      {tag}
    </div>
  ));

  return (
    <div className="container mt-4">
      {imageCarousel}

      <div className="mt-4">
        <p>
          <b>Title:</b> {project.title}
        </p>
        <p>
          <b>Description:</b> {project.description}
        </p>
        <p>
          <b>Date:</b> {project.date}
        </p>
        <p>
          <b>Tags:</b> {tags}
        </p>
        <p>
          <b>Website:</b> <a href={project.website}>website</a>
        </p>
        <p>
          <b>GitHub:</b> <a href={project.github}>github</a>
        </p>

        {project.demo ? (
          <Link to={`/projects/${project.slug}/demo`}>Demo</Link>
        ) : (
          <p>Demo goes here</p>
        )}
      </div>
    </div>
  );
};

export default Show;
