import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import projectsJSON from '../../assets/data/projects.json';
import { Link } from 'react-router-dom';
const Show = () => {
  const [project, setProject] = useState(null);
  const { slug } = useParams();



  const tags = project?.tags?.map((tag, i) => {
    return <div key={i} className="badge badge-outline badge-primary">{tag}</div>;
  });


  useEffect(() => {
    // let temp = projectsJSON.filter((project) => {
    //   return project.slug === slug;
    // });

    setProject(projectsJSON.find(project => project.slug === slug));
  }, [slug]);

  if(!project) return <h1>Project doesnt exist</h1>;


  let imageCarousel = "";

  if(project.images){

    let items = project.images.map((image, i) => {
      return (
        <div key={i} id={`item${i}`} className="carousel-item w-full">
          <img title={image.caption} src={`${image.path}`} className="w-full" alt={image.caption} />
        </div> 
      );
    });

    let buttons = project.images.map((image, i) => {
      return (
        <Link key={i} to={`#item${i}`} className="btn btn-xs">{i+1}</Link> 
      );
    });

    imageCarousel = (
      <>
      <div className="carousel w-full">
        {items}
      </div> 
      <div className="flex justify-center w-full py-2 gap-2">
        {buttons}
      </div>
      </>
    );
  }

  return (
    <>
      <p><b>Title:</b> {project.title}</p>
      <p><b>Description:</b> {project.description}</p>
      {imageCarousel}

      <p><b>Date:</b> {project.date}</p>
      <p><b>Tags:</b> {tags}</p>
      <p><b>Website:</b> <Link to={project.website}>website</Link></p>
      <p><b>GitHub:</b> <Link to={project.github}>github</Link></p>

      {project.demo ? (
        <Link to={`/projects/${project.slug}/demo`}>Demo</Link>
) : (
  <p>Demo goes here</p>

)}

    </>
  );
};

export default Show;
