import React, { useEffect, useState } from 'react';

const Contact = () => {
  const [isNavHidden, setNavHidden] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setNavHidden((prev) => window.scrollY > 0 && window.scrollY > prev);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <head>
        <meta charSet="utf-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <link rel="stylesheet" href="/css/main.css" />
        <link rel="stylesheet" href="/node_modules/bootstrap-icons/font/bootstrap-icons.css" />
        <link href="https://cdnjs.cloudflare.com/ajax/libs/mdb-ui-kit/4.2.0/mdb.min.css" rel="stylesheet" />
        <link rel="icon" href="/resources/images/code-solid.svg" type="image/gif" />
        <title>About Us</title>
      </head>

      <body>


        {/* Title w/ Full width background */}
        <div className="bg-off-white text-light pt-3 img width-auto">
          <div className="container-fluid">
            <div className="container">
              <div className="row pt-5">
                <div>
                  <p className="pt-5 mb-5 text-center text-dark display-1 display-title col-12 ">About Me</p>
                </div>
              </div>
            </div>
          </div>

          {/* Nested containers with full width backgrounds colours to give clear definition of different articles */}
          <div className="container-fluid bg-orange">
            <div className="container">
              <div className="row">
                <div className="my-3 my-md-5  col-12 col-md-6 ">
                  <img className="med-card cover" src="/resources/images/in-the-studio.webp" alt="Two men in a music studio" />
                </div>
                <h2 className="col-12 col-md-6  text-dark my-3 my-md-5">My name is Jack a </h2>
              </div>
            </div>
          </div>
          
          {/* ... (Repeat similar structure for other sections) */}

        </div>

        {/* Footer */}


        {/* Bootstrap JS */}
        <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.2.2/dist/js/bootstrap.bundle.min.js" 
                integrity="sha384-OERcA2EqjJCMA+/3y+gxIOqMEjwtxJY7qPCqsdltbNJuaOe923+mo//f6V8Qbsw3" 
                crossOrigin="anonymous"></script>
      </body>
    </>
  );
};

export default Contact;
