import React, { useState } from "react";
import { Link } from "react-router-dom";

const Sidebar = () => {
	const [open, setOpen] = useState(true);

	const toggleSidebar = () => {
		setOpen(!open);
	};

	return (
		<div className={`flex bg-slate-500 ${open ? "w-72" : "w-20"}`}>
			<div className="bg-dark-purple h-screen p-5 pt-8 relative duration-300">
				<div
					className={`absolute cursor-pointer -right-3 top-9 w-7 border-dark-purple border-2  ${
						!open && "rotate-180"
					}`}
					alt="Toggle Sidebar"
					onClick={toggleSidebar}
				>
					<h1>&gt;</h1>
				</div>
				<div className={`flex gap-x-4 items-center ${!open && "scale-0"}`}>
					<h1 className="text-white origin-left font-medium text-xl duration-200">
						Jack
					</h1>
				</div>
				<ul className="pt-6">
					<li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4">
						<Link to="/">Home</Link>
					</li>
					<li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4">
						<Link to="/about">About</Link>
					</li>
					<li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4">
						<Link to="/projects">Projects</Link>
					</li>
					<li className="m-2 btn btn-ghost flex rounded-md p-2 cursor-pointer text-gray-300 text-sm gap-x-4">
						<Link to="/contact">Contact</Link>
					</li>
				</ul>
			</div>
		</div>
	);
};

export default Sidebar;
