import { Link } from 'react-router-dom';
import { useState,useEffect } from 'react';
import '../assets/app.css'
import { useLocation } from 'react-router-dom';
import { string } from 'mathjs';

const Navbar = () => {
  const [isNavHidden, setIsNavHidden] = useState(false);
  const [location, setLocation] = useState('false');

  
  let thisLocation = useLocation()
  const NavReduce = () => {
    let lastScrollY = window.scrollY;

    window.addEventListener("scroll", () => {
      if (lastScrollY < window.scrollY) {
        setIsNavHidden(true);
      } else {
        setIsNavHidden(false);
      }
      lastScrollY = window.scrollY;
    });
  };

  return (
    <div className={``}>
          {NavReduce()}

      <div className={`fixed top-0 ${isNavHidden ? 'hide-nav' : 'show-nav'}`}>
        <div className="dropdown">
          <ul  className="flex  text-white  w-52">
                <li className="m-2 hover:underline text-5xl underline-offset-8">
              {thisLocation.path}
            </li>
            <li className="m-2 hover:underline  underline-offset-8 ">
              <Link to="/">Home</Link>
            </li>

            <li className="m-2 hover:underline underline-offset-8">
              <Link to="/projects">Projects</Link>
            </li>


          </ul>
        </div>
      </div>

    </div>
  );
};

export default Navbar;
