import React, { useState } from "react";
import projectsJSON from "../../assets/data/projects.json";
import {getDatabase} from "firebase/database"
import ProjectCard from "../../components/ProjectCard";
import { useEffect } from "react";
import axios from 'axios';
const Index = () => {
	const [projects, setProjects] = useState(projectsJSON);
	const [filteredProjects, setFilteredProjects] = useState(projectsJSON);
	const [selectedOption, setSelectedOption] = useState("All");

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.post('https://eu-central-1.aws.data.mongodb-api.com/app/data-jzgpz/endpoint/data/v1/action/findOne', {
                    "collection": "Projects",
                    "database": "Portfolio",
                    "dataSource": "Cluster0",
                    "projection": {
                        "_id": 1
                    }
                }, {
                    headers: {
                        'Content-Type': 'application/json',
                        'Access-Control-Request-Headers': '*',
                        'api-key': 'vwvSLHNFKkFCrL8eSgaqBXJ4oh6UEZTjY0nNmOJBsgQimv3r5i3RNxg7pAjkTn7C',
                    },
                });

                setProjects([response.data]); // Assuming your response is an array of projects, modify this accordingly
                setFilteredProjects([response.data]);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []); // The empty dependency array ensures that this effect runs once when the component mounts

    const projectList = filteredProjects.map((project, i) => {
        return <ProjectCard key={i} project={project} cardKey={i} />;
    });

    const filterProjects = (filter) => {
        setSelectedOption(filter);

        if (filter === "All") {
            setFilteredProjects(projects);
        } else {
            const filtered = projects.filter((project) =>
                project.tags.includes(filter)
            );
            setFilteredProjects(filtered);
        }
    };

    const tags = [
        "All",
        "JavaScript",
        "React",
        "HTML",
        "Front-End",
        "Rest Client",
        "API",
        "CSS",
        "Tailwind",
        "Python"
    ];

	return (
		<div className="justify-items-center">
			<div className="container">
				<div className="text-6xl flex justify-center font-light py-16">
					Myâ€…Projects
				</div>
			<div className="relative text-left flex justify-center">
					<button
						type="button"
						className="btn-dropdown"
						onClick={() => filterProjects(selectedOption)}
					>
					</button>
					<div className=" flex justify-center my-3">

							{tags.map((option) => (
								<div
									key={option}
									className="badge mx-1 cursor-pointer p-3 badge-outline "
									role="menuitem"
									onClick={() => filterProjects(option)}
								>
									{option}
								</div>
							))}
					</div>

				</div>
			</div>


			<div className="">{projectList}</div>
		</div>
	);
};

export default Index;
