import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import projectsJSON from "../../assets/data/projects.json";
import { Link } from "react-router-dom";

const Show = () => {
  const [project, setProject] = useState(null);
  const { slug } = useParams();

  const tags = project?.tags?.map((tag, i) => (
    <div key={i} className="badge badge-outline badge-primary">
      {tag}
    </div>
  ));

  useEffect(() => {
    setProject(projectsJSON.find((project) => project.slug === slug));
  }, [slug]);

  if (!project) return <h1>Project doesn't exist</h1>;

  let imageCarousel = "";

  if (project.images) {
    let items = project.images.map((image, i) => (
      <div key={i} className={`carousel-item${i === 0 ? ' active' : ''}`}>
        <img
          title={image.caption}
          src={`${image.path}`}
          className="d-block w-100"
          alt={image.caption}
        />
      </div>
    ));

    let buttons = project.images.map((image, i) => (
      <button
        key={i}
        type="button"
        data-bs-target="#carouselExample"
        data-bs-slide-to={i}
        className={`btn btn-sm${i === 0 ? ' active' : ''}`}
      >
        {i + 1}
      </button>
    ));

    imageCarousel = (
      <div id="carouselExample" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">{items}</div>
        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#carouselExample"
          data-bs-slide="prev"
        >
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#carouselExample"
          data-bs-slide="next"
        >
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
        <div className="d-flex justify-content-center">{buttons}</div>
      </div>
    );
  }

  return (
    <>
      {imageCarousel}

      <p>
        <b>Title:</b> {project.title}
      </p>
      <p>
        <b>Description:</b> {project.description}
      </p>
      <p>
        <b>Date:</b> {project.date}
      </p>
      <p>
        <b>Tags:</b> {tags}
      </p>
      <p>
        <b>Website:</b> <Link to={project.website}>website</Link>
      </p>
      <p>
        <b>GitHub:</b> <Link to={project.github}>github</Link>
      </p>

      {project.demo ? (
        <Link to={`/projects/${project.slug}/demo`}>Demo</Link>
      ) : (
        <p>Demo goes here</p>
      )}
    </>
  );
};

export default Show;
